import React, { useEffect, useState } from "react";
import { NavLink, Outlet, useParams } from "react-router-dom";
import ShiftMissionList from "../components/ShiftDetail/ShiftMissionList";
import instanceAxios from "../api";
import NavBar from "../components/UI/Navbar";
import CircularButton from "../components/UI/CircularButton";
import Modal from "../components/UI/Modal";
import Button from "../components/UI/Button";

const shiftsDummyData = {
  id: 1,
  shift_date: "2022-05-10 21:00:00",
  shift_dispatcher: "Karamjeet",
  status: 1,
};

const missionsDummyData = {
  mission_list: [
    {
      id: 1,
      shift_id: 1,
      missionType: { id: 1, name: "Rebalancing" },
      quantity: "4",
      vins: "554, 443, 234, 1898",
      pickUp: "Rue de la paix",
      dropOff: "Rue de la guerre",
      fleet: "dispatch",
      comment: "déplacer ces scooters",
      status: 1,
    },
    {
      id: 2,
      shift_id: 1,
      missionType: { id: 2, name: "Rebalancing" },
      quantity: "4",
      vins: "554, 443, 234, 1898",
      pickUp: "Rue de la paix",
      dropOff: "Rue de la guerre",
      fleet: "dispatch",
      comment: "déplacer ces scooters",
      status: 2,
    },
    {
      id: 3,
      shift_id: 1,
      missionType: { id: 3, name: "Dispatch" },
      quantity: "4",
      vins: "554, 443, 234, 1898",
      pickUp: "Rue de la paix",
      dropOff: "Rue de la guerre",
      fleet: "dispatch",
      comment: " ces scooters",
      status: 1,
    },
  ],
};

//TODO récupérer données du shift + les afficher + binder la cloture de shift avec le status du shift
const ShiftDetail = (props) => {
  const params = useParams();
  console.log(params.shiftId);
  console.log("start rendering");
  //const [missions, setMissions] = useState([]);
  const [missions, setMissions] = useState(missionsDummyData);
  const [generalInformation, setGeneralInformation] = useState(shiftsDummyData);
  const [isAddingMission, setIsAddingMission] = useState(false);
  const [isButtonPressed, setIsButtonPressed] = useState(false);

  // useEffect(() => {
  //   instanceAxios.get("missions.json").then((response) => {
  //     setMissions(response.data);
  //   });
  // }, []);

  // useEffect(() => {
  //   instanceAxios.get("shifts.json").then((response) => {
  //     response.data.map((shift, index) => {
  //       if (shift.id == params.shiftId) {
  //         console.log(shift);
  //         setGeneralInformation(shift);
  //       }
  //     });
  //   });
  // }, []);

  const addMissionHandler = () => {
    setIsButtonPressed(true);
  };

  const confirmHandler = () => {
    setIsAddingMission(true);
    return "blabla";
  };

  const cancelHandler = () => {
    setIsAddingMission(false);
  };
  console.log(missions);
  console.log(generalInformation);

  return (
    <>
      <NavBar></NavBar>


      <div className="w-5/6 max-w-6xl sm:w-3/4 lg:w-1/2 flex justify-center flex-col items-center">
        <div className="gap-4 py-8 sm:px-8 xl:px-0">
          <div className="pb-3 sm:pb-2">
            <h1 className="text-2xl font-bold leading-6 text-black ">
              Shift detail
            </h1>
            {shiftsDummyData && (
              <>
                <p>Date: {shiftsDummyData.shift_date}</p>
                <p>Dispatcheur: {shiftsDummyData.shift_dispatcher}</p>
              </>
            )}
          </div>
        </div>

          <ShiftMissionList
            missions={missions.mission_list}
            shiftId={params.shiftId}
          ></ShiftMissionList>

      </div>
      <div className="w-5/6 max-w-6xl sm:w-3/4 lg:w-1/2 flex mx-auto mb-8 ">
        <div className="flex w-full justify-between">
          <NavLink to="add-mission">
            <Button onClick={addMissionHandler} style="primary">
              Add extra
            </Button>
          </NavLink>
          <Button onClick={addMissionHandler} style="danger">
            Close shift
          </Button>
        </div>
      </div>
      {isButtonPressed ? (
        <Modal onConfirm={confirmHandler} onCancel={cancelHandler}></Modal>
      ) : (
        <> </>
      )}
      {isAddingMission ? <Outlet></Outlet> : <></>}
    </>
  );
};
export default ShiftDetail;
