import React, { useReducer, useState } from "react";
import ReactDom from "react-dom";
import Button from "../UI/Button";
import Card from "../UI/Card";
import Input from "../UI/Input";

const AddUser = (props) => {
  const [typedDispatcherName, setDispatcherName] = useState("");
  const [typedDate, setDate] = useState("");
  const [selectedMissionType, setMissionType] = useState("");
  const [typedQuantity, setQuantity] = useState("");
  const [typedVehicleIds, setVehicleIds] = useState("");
  const [typedPickUpAdress, setPickUpAdress] = useState("");
  const [typedDropOffAdress, setDropOffAdress] = useState("");
  const [typedFleet, setFleet] = useState(true);
  const [typedComment, setComment] = useState(true);

  const addMissionHandler = (event) => {
    event.preventDefault();
    console.log(
      selectedMissionType,
      typedQuantity,
      typedVehicleIds,
      typedPickUpAdress,
      typedDropOffAdress,
      typedFleet,
      typedComment
    );
  };

  const missionSelectedChangeHandler = (event) => {
    setMissionType(event.target.value);
  };
  const quantityChangeHandler = (event) => {
    setQuantity(event.target.value);
  };
  const vehicleIdsChangeHandler = (event) => {
    setVehicleIds(event.target.value);
  };
  const pickUpAdressChangeHandler = (event) => {
    setPickUpAdress(event.target.value);
  };
  const dropOffAdressChangeHandler = (event) => {
    setDropOffAdress(event.target.value);
  };
  const fleetChangeHandler = (event) => {
    setFleet(event.target.value);
  };
  const commentChangeHandler = (event) => {
    setComment(event.target.value);
  };

  return (
    <>
      <Card className="w-96">
        <div className="grid grid-cols-2 gap-4 pb-8">
          <div>
            <label>Créneau de tournée</label>
            <input
              type="datetime-local"
              name="shift_date"
              id="shift_date"
              required
            ></input>
          </div>
          <div>
            <label className="text-gray-700">Dispatcher name:</label>
            <input type="text" name="dispatcherName"></input>
          </div>
        </div>
        <form
          className="grid auto-rows-auto gap-4"
          onSubmit={addMissionHandler}
        >
          <label className="text-gray-700">Type de mission</label>
          <select
            id="mission_type"
            name="carmission_types"
            onChange={missionSelectedChangeHandler}
          >
            <option value="rebalancing">Rebalancing</option>
            <option value="clusters">Clusters</option>
            <option value="towing">Towing</option>
            <option value="impoundment">Impoundment dispatch</option>
            <option value="out_of_zone">Out of zone towing</option>
            <option value="internal_dispatch">Internal Dispatch</option>
          </select>
          <label>Quantité</label>
          <input
            type="text"
            name="quantity"
            onChange={quantityChangeHandler}
          ></input>
          <label>VINs</label>
          <input
            type="text"
            name="vehicle_ids"
            onChange={vehicleIdsChangeHandler}
          ></input>
          <label>Enlèvement</label>
          <input
            type="text"
            name="pick_up"
            onChange={pickUpAdressChangeHandler}
          ></input>
          <label>Dépôt</label>
          <input
            type="text"
            name="drop_off"
            onChange={dropOffAdressChangeHandler}
          ></input>
          <label>Flotte</label>
          <input type="text" name="fleet" onChange={fleetChangeHandler}></input>
          <label>Commentaire</label>
          <input
            type="text"
            name="comment"
            onChange={commentChangeHandler}
          ></input>

          {/* <Input
            isValid={isValid}
            type="text"
            name="userName"
            value={enterredUserName}
            onChange={userNameChangeHandler}
          />
          <label htmlFor="age" className="text-gray-700">
            Age:{" "}
          </label>
          <Input
            isValid={isValid}
            type="number"
            name="age"
            value={enterredAge}
            onChange={ageChangeHandler}
          /> */}
          <Button type="submit">Submit</Button>
        </form>
      </Card>
    </>
  );
};
export default AddUser;
