/* This example requires Tailwind CSS v2.0+ */
import { Fragment, useState } from "react";
import { Listbox, Transition } from "@headlessui/react";
import { CheckIcon, SelectorIcon } from "@heroicons/react/solid";
import ParisIcon from "../../Badges/Paris.svg";
import MilanoIcon from "../../Badges/Milan.svg";
import BarcelonaIcon from "../../Badges/Barcelona.svg";
import NiceIcon from "../../Badges/Nice.svg";

function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

const citiesAtributes = [
  {
    name: "Paris",
    icon: ParisIcon,
  },
  {
    name: "Milano",
    icon: MilanoIcon,
  },
  {
    name: "Barcelona",
    icon: BarcelonaIcon,
  },
  {
    name: "Nice",
    icon: NiceIcon,
  },
];

const DropdownCity = (props) => {
  const [selected, setSelected] = useState(props.selected);

  const dropDownSelectionHandler = (event) => {
    setSelected(event);
  };

  return (
    <Listbox value={selected} onChange={dropDownSelectionHandler}>
      {({ open }) => (
        <>
          <div className="relative">
            <div className="mt-1 relative">
              <Listbox.Button className="bg-gray-800 flex text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white">
              <img
                        className="h-8 w-8 rounded-full"
                        src={ParisIcon}
                        alt=""
                      />
              </Listbox.Button>

              {Array.isArray(citiesAtributes) ? 

              <Transition
                show={open}
                as={Fragment}
                leave="transition ease-in duration-100"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
              >
                <Listbox.Options className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none text-sm">
                  {citiesAtributes.map((city) => (
                    <Listbox.Option
                      key={city.id}
                      className={({ active }) =>
                        classNames(
                          active ? "text-white bg-blue-600" : "text-gray-900",
                          "cursor-default select-none relative py-2 pl-3 pr-9"
                        )
                      }
                      value={city.name}
                    >
                      {({ selected, active }) => (
                        <>
                          <span
                            className={classNames(
                              selected ? "font-semibold" : "font-normal",
                              "block truncate"
                            )}
                          >
                            {city.name}
                          </span>
                          <p>AA</p>

                          {selected ? (
                            <span
                              className={classNames(
                                active ? "text-white" : "text-blue-600",
                                "absolute inset-y-0 right-0 flex items-center pr-4"
                              )}
                            >
                              <CheckIcon
                                className="h-5 w-5"
                                aria-hidden="true"
                              />
                            </span>
                          ) : null}
                        </>
                      )}
                    </Listbox.Option>
                  ))}
                </Listbox.Options>
              </Transition>
              : <></>
            }

            </div>
          </div>
        </>
      )}
    </Listbox>
  );
};
export default DropdownCity;
