import { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import Input from "../UI/Input";
import "./AddUser.css";
import { toast } from 'react-toastify';

toast.configure();
const AddUser = (props) => {
  const [enterredUserName, setEnterredUserName] = useState("");
  const [enterredAge, setEnterredAge] = useState("");
  const [isValid, setIsValid] = useState(true);

  const addUserHandler = (event) => {
    event.preventDefault();
    if (
      enterredUserName.trim().length === 0 ||
      enterredAge.trim().length === 0
    ) {
      setIsValid(false);
      toast.error('Something went wrong!', {position: toast.POSITION.TOP_CENTER})
      return;
    }
    if (+enterredAge < 1) {
      setIsValid(false);
      return;
    }
    props.onAdd(enterredUserName, enterredAge);
    setIsValid(true);
    setEnterredAge("");
    setEnterredUserName("");
  };

  const userNameChangeHandler = (event) => {
    setIsValid(true);
    setEnterredUserName(event.target.value);
  };

  const ageChangeHandler = (event) => {
    setIsValid(true);
    setEnterredAge(event.target.value);
  };

  return (
    <div>
      <ErrorModal isValid={isValid}></ErrorModal>
      <Card className="w-96">
        <form className="grid auto-rows-auto gap-4" onSubmit={addUserHandler}>
          <label htmlFor="userName" className="text-gray-700">
            User name:{" "}
          </label>
          <Input
            isValid={isValid}
            type="text"
            name="userName"
            value={enterredUserName}
            onChange={userNameChangeHandler}
          />
          <label htmlFor="age" className="text-gray-700">
            Age:{" "}
          </label>
          <Input
            isValid={isValid}
            type="number"
            name="age"
            value={enterredAge}
            onChange={ageChangeHandler}
          />
          <Button type="submit">Submit</Button>
        </form>
      </Card>
    </div>
  );
};
export default AddUser;
