import React from "react";

const Input = (props) => {
  var required = {};
  if (props.requiredField) {
    required = { required: "required" };
  }
  console.log({ ...required });
  return (
    <>
    <div className={`mt-2 py-2 px-3 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border border-gray-300 rounded-md  ${props.className}`}>
      <label
        htmlFor={props.labelHtmlFor}
        className="absolute -top-2 left-2 -mt-px inline-block px-1 bg-white text-xs font-medium text-gray-900"
      >
        {props.content}
      </label>
      <input
        type={props.type}
        name={props.name}
        className="block w-full border-0 p-0 text-gray-900 placeholder-gray-500 focus:ring-0 sm:text-sm"
        placeholder={props.placeholder}
        onChange={props.onChange}
        {...required}
        >
        {props.children}
      </input>
    </div>
    <div className="relative border border-gray-300 rounded-md px-3 py-2 shadow-sm focus-within:ring-1 focus-within:ring-indigo-600 focus-within:border-indigo-600">
      <label
        htmlFor="name"
        className="absolute -top-2 left-2 -mt-px inline-block px-1 bg-white text-xs font-medium text-gray-900"
      >
        Name
      </label>
      <input
        type="text"
        name="name"
        id="name"
        className="block w-full border-0 p-0 text-gray-900 placeholder-gray-500 focus:ring-0 sm:text-sm"
        placeholder="Jane Smith"
      />
    </div>
      <input
        type={props.type}
        name={props.name}
        className={`mt-2 py-2 px-3 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border border-gray-300 rounded-md `}
        value={props.value}
        onChange={props.onChange}
        {...required}
      >
        {props.children}
      </input>
    </>
  );
};
export default Input;
