import React, { useState } from "react";
import ShiftHeader from "./ShiftHeader";
import MissionList from "./MissionList";
import Card from "../UI/Card";
import Button from "../UI/Button";
import Input from "../UI/Input";

const ShiftSummary = (props) => {

  const [shiftDate, setShiftDate] = useState("")
  const [dispatcher, setDispatcher] = useState("")

  const shiftDateChangeHandler = (event) => {
    console.log(event.target.value)
    setShiftDate(event.target.value)
  }

  const dispatcherChangeHandler = (event) => {
    console.log(event.target.value)
    setDispatcher(event.target.value)
  }

  return (
    <div className="grid auto-rows-min w-full">
      <div className="pb-3 sm:pb-2">
        <h1 className="text-lg font-medium leading-6 text-gray-900 ">
          Shift summary
        </h1>
      </div>
      <Card className="">
        <form onSubmit={props.shiftInformationHandler}>
        <div className="grid grid-cols-6 gap-4 px-4 py-5 sm:p-6">
        <div className="col-span-6 lg:col-span-3">
          <Input
            labelContent="CrÃ©neau"
            type="datetime-local"
            name="shift_date"
            id="shift_date"
            required
            onChange={shiftDateChangeHandler}
          ></Input>
        </div>
        <div className="col-span-6 lg:col-span-3">
          <Input
            labelContent="Dispatcher name"
            type="text"
            name="dispatcherName"
            onChange={dispatcherChangeHandler}
          ></Input>
        </div>
      </div>
          {/* <ShiftHeader
            shiftInformationHandler={props.shiftInformationHandler}
          /> */}
          {props.missionList.length > 0 && (
            <>
              <MissionList
                missionList={props.missionList}
                deleteHandler={props.deleteHandler}
              ></MissionList>
              <div className="py-3 bg-gray-50 text-right sm:px-6">
                <Button type="submit">Send</Button>
              </div>
            </>
          )}
        </form>
      </Card>
    </div>
  );
};
export default ShiftSummary;
