import { configureStore } from "@reduxjs/toolkit"


const dataReducer = (state = {city: {name: "unknown", icon: ""}}, action) => {

    if (action.type === 'cityChange')
    {
        return{
            city: {name: "Paris", icon: ""}
        }
    }

    return state;
}

const store = configureStore(dataReducer);
export default store

const cityChange = (payload) => ({
  type: "cityChange",
  payload
})
