import { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import Input from "../UI/Input";
import "./AddUser.css"
const AddUser = () => {
    const [enterredUserName, setEnterredUserName] = useState('');
    const [enterredUserAge, setEnterredUserAge] = useState('');

    const addUserHandler = (event) => {
        event.preventDefault();
    }

    const userNameChangeHandler = (event) => {
        setEnterredUserName(event.target.value);
        console.log(enterredUserName);
    }

    return(
        <div className="antialiased text-gray-900 px-6">
        <div className="mt-8 max-w-md">    
        <Card>
            <form className="input flex flex-col space-y-4 items-start " onSubmit={addUserHandler}>
                <label htmlFor="userName" className="text-gray-700">User name: </label>
                <input type="text" name="userName" className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 h-10 px-2" onChange={userNameChangeHandler}></input>
                <label htmlFor="age" className="text-gray-700">Age: </label>
                <Input type="number" name="age" className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 h-10 px-2" onChange={userNameChangeHandler}/>
                <input type="number" name="age" className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 h-10 px-2" onChange={userNameChangeHandler}></input>
                <Button type="submit">Submit</Button>
            </form>
        </Card>
        </div>

        </div>

    )
}
export default AddUser;
