import { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import Input from "../UI/Input";
import "./AddUser.css"
const AddUser = (props) => {
    const [enterredUserName, setEnterredUserName] = useState('');
    const [enterredAge, setEnterredAge] = useState('');
    const [isValid, setIsValid] = useState(true);

    const addUserHandler = (event) => {
        event.preventDefault();
        if(enterredUserName.trim().length === 0 || enterredAge.trim().length === 0){
            setIsValid(false)
            return;
        }
        if(+enterredAge < 1){
            setIsValid(false)
            return;
        }
        props.onAdd(enterredUserName, enterredAge);
        setIsValid(true)
        setEnterredAge('');
        setEnterredUserName('');
    }

    const userNameChangeHandler = (event) => {
        setIsValid(true)
        setEnterredUserName(event.target.value);
    }

    const ageChangeHandler = (event) => {
        setIsValid(true)
        setEnterredAge(event.target.value);
    }

    return(
        <Card>
            <form className="input flex flex-col space-y-4 items-start " onSubmit={addUserHandler}>
                {!isValid && <p className="text-red-500">erreur</p>}
                <label htmlFor="userName" className="text-gray-700">User name: </label>
                <Input isValid={isValid} type="text" name="userName" value={enterredUserName} onChange={userNameChangeHandler}/>
                <label htmlFor="age" className="text-gray-700">Age: </label>
                <Input isValid={isValid} type="number" name="age" value={enterredAge} onChange={ageChangeHandler}/>
                <Button type="submit">Submit</Button>
            </form>
        </Card>    
    )
}
export default AddUser;
