import { createSlice } from "@reduxjs/toolkit";

//reducer
export const shiftSlice = createSlice({
  name: "shiftSlice",
  initialState: {},
  reducers: {
    setSelectedShift: (state, action) => {
        state = action.payload
  },
}})
