import React from "react";

const Input = (props) => {
  var required = {};
  if (props.requiredField) {
    required = { required: "required" };
  }
  console.log({ ...required });
  return (
    <>
      <input
        type={props.type}
        name={props.name}
        className={`mt-2 focus:ring-indigo-500 focus:border-indigo-500 block w-full shadow-sm sm:text-sm border border-gray-300 rounded-md ${props.className}`}
        value={props.value}
        onChange={props.onChange}
        {...required}
      >
        {props.children}
      </input>
    </>
  );
};
export default Input;
