import React, { useState } from "react";
import ReactDom from "react-dom";
import Button from "../UI/Button";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const AddUser = (props) => {
  const [enterredUserName, setEnterredUserName] = useState("");
  const [enterredAge, setEnterredAge] = useState("");
  const [isValid, setIsValid] = useState(true);

  const notify = (errorMessage) => {
    toast.error(errorMessage, {
      position: "top-right",
      autoClose: 1000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };

  const addUserHandler = (event) => {
    event.preventDefault();
    if (
      enterredUserName.trim().length === 0 ||
      enterredAge.trim().length === 0
    ) {
      notify("Please enter a valid name and age (no empty values)");
      setIsValid(false);
      return;
    }
    if (+enterredAge < 1) {
      notify("Negative ages not allowed");
      setIsValid(false);
      return;
    }
    props.onAdd(enterredUserName, enterredAge);
    setIsValid(true);
    setEnterredAge("");
    setEnterredUserName("");
  };

  const userNameChangeHandler = (event) => {
    setIsValid(true);
    setEnterredUserName(event.target.value);
  };

  const ageChangeHandler = (event) => {
    setIsValid(true);
    setEnterredAge(event.target.value);
  };

  return (
    <>
      {ReactDom.createPortal(
        <ToastContainer />,
        document.getElementById("toaster")
      )}
      <Card className="w-96">
        <div className="grid grid-cols-2 gap-4 pb-8">
          <div>
            <label>Créneau de tournée</label>
            <input
              type="datetime-local"
              name="shift_date"
              id="shift_date"
              required
            ></input>
          </div>
          <div>
            <label className="text-gray-700">Dispatcher name:</label>
            <input type="text" name="dispatcherName"></input>
          </div>
        </div>
        <form className="grid auto-rows-auto gap-4" onSubmit={addUserHandler}>
          <label className="text-gray-700">Type de mission</label>
          <select id="mission_type" name="carmission_types">
            <option value="rebalancing">Rebalancing</option>
            <option value="clusters">Clusters</option>
            <option value="towing">Towing</option>
            <option value="impoundment">Impoundment dispatch</option>
            <option value="out_of_zone">Out of zone towing</option>
            <option value="internal_dispatch">Internal Dispatch</option>
          </select>
          <label>Quantité</label>
          <input type="text" name="quantity"></input>
          <label>VINs</label>
          <input type="text" name="vehicle_ids"></input>
          <label>Enlèvement</label>
          <input type="text" name="pick_up"></input>
          <label>Dépôt</label>
          <input type="text" name="drop_off"></input>
          <label>Flotte</label>
          <input type="text" name="fleet"></input>
          <label>Commentaire</label>
          <input type="text" name="comment"></input>

          {/* <Input
            isValid={isValid}
            type="text"
            name="userName"
            value={enterredUserName}
            onChange={userNameChangeHandler}
          />
          <label htmlFor="age" className="text-gray-700">
            Age:{" "}
          </label>
          <Input
            isValid={isValid}
            type="number"
            name="age"
            value={enterredAge}
            onChange={ageChangeHandler}
          /> */}
          <Button type="submit">Submit</Button>
        </form>
      </Card>
    </>
  );
};
export default AddUser;
