import React, { useEffect, useState } from "react";
import Card from "../UI/Card";
import Dropdown from "../UI/Dropdown";
import Input from "../UI/Input";
import Button from "../UI/Button";
import instanceAxios from "../../api";

const AnswerForm = (props) => {
  const submitAnswerHandler = () => {};
  const [answer, setAnswer] = useState({});
  const [missions, setMissions] = useState({
    id: 1,
    shift_id: 1,
    missionType: { id: 1, name: "Rebalancing" },
    quantity: "4",
    vins: "554, 443",
    pickUp: "Rue de la paix",
    dropOff: "Rue de la guerre",
    fleet: "dispatch",
    comment: "déplacer ces scooters",
    status: 1,
  });
  useEffect(() => {
    instanceAxios.get("mission_type.json").then((response) => {
      console.log(response.data.missionTypes);
    });
  }, [props]);

  const vehicleIdsChangeHandler = (event) => {
    setAnswer((prevAnswer) => ({ ...prevAnswer, vins: event.target.value }));
  };
  console.log(answer);

  return (
    <>
      <form onSubmit={submitAnswerHandler}>
        <Card className="">
          <div className="grid grid-cols-6 gap-4 px-4 py-5 sm:p-6">
            <Input
              labelContent="Scooter numbers"
              type="text"
              name="vehicle_ids"
              onChange={vehicleIdsChangeHandler}
              value={props.mission.vins}
              requiredField={true}
            ></Input>
          </div>
          <div className="py-3 bg-gray-50 text-right px-2 sm:px-6">
            <Button type="submit">Submit</Button>
          </div>
        </Card>
      </form>
    </>
  );
};
export default AnswerForm;
