import React from "react";

const Button = (props) => {
  const style = ""

  switch(props.type){
    case "primary": 
    style = " text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
    break;
    case "danger":
      style = " text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
      break;
    case "success":
      style = " text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
  }

  return (
    <button
      className={`inline-flex items-center px-5 py-2 border border-transparent text-base font-medium rounded-xl shadow-sm ${style} ${props.className ? props.className : ""}`}
      type={props.type || "button"}
      onClick={props.onClick}
    >
      {props.children}
    </button>
  );
};
export default Button;
