import React from "react";

const Input = (props) => {
  var required = {};
  if (props.requiredField) {
    required = { required: "required" };
  }
  //console.log({ ...required });
  return (
    <>
    <div className="relative">
    <label
        htmlFor="name"
        className="absolute -top-2 left-2 -mt-px inline-block px-1 bg-white text-xs font-medium text-gray-900"
      >
         {props.labelContent}
      </label>
      <input
        type={props.type}
        name={props.name}
        className={`mt-2 py-2 px-3 focus:border-blue-500 focus:border-blue-500 focus:border block w-full shadow-sm sm:text-sm border border-gray-300 rounded-md `}
        value={props.value}
        onChange={props.onChange}
        {...required}
        >
        {props.children}
      </input>
        </div>
    </>
  );
};
export default Input;
