import { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import Input from "../UI/Input";
import "./AddUser.css"
const AddUser = () => {
    const [enterredUserName, setEnterredUserName] = useState('');
    const [enterredAge, setEnterredAge] = useState('');

    const addUserHandler = (event) => {
        event.preventDefault();
        if(enterredUserName.trim.length === 0 || enterredAge.trim.length === 0){
            return;
        }
        if(+enterredAge < 1){
            return;
        }
        console.log(enterredUserName, enterredAge);
        setEnterredAge('');
        setEnterredUserName('');
    }

    const userNameChangeHandler = (event) => {
        setEnterredUserName(event.target.value);
    }

    const ageChangeHandler = (event) => {
        setEnterredAge(event.target.value);
    }

    return(
        <div className="antialiased text-gray-900 px-6">
        <div className="mt-8 max-w-md">    
        <Card>
            <form className="input flex flex-col space-y-4 items-start " onSubmit={addUserHandler}>
                <label htmlFor="userName" className="text-gray-700">User name: </label>
                <Input type="text" name="userName" value={enterredUserName} onChange={userNameChangeHandler}/>
                <label htmlFor="age" className="text-gray-700">Age: </label>
                <Input type="number" name="age" value={enterredAge} onChange={ageChangeHandler}/>
                <Button type="submit">Submit</Button>
            </form>
        </Card>
        </div>

        </div>

    )
}
export default AddUser;
