import { createStore } from "redux"


const dataReducer = (state = {city: {name: "unknown", icon: ""}}, action) => {

    if (action.type === 'cityChange')
    {
        return{
            city: {name: "Paris", icon: ""}
        }
    }

    return state
}

const store = createStore()

export const cityChange = (payload) => ({
  type: "cityChange",
  payload
})
